package ru.hh.school.apiGetText;

import static io.restassured.RestAssured.*;

import io.restassured.*;
import io.restassured.http.Method;
import io.restassured.response.Response;
import io.restassured.specification.RequestSpecification;
import org.testng.Assert;
import org.testng.annotations.Test;

public class HhApiGetTest {
    
    @Test
    void NormalText() {
        RequestSpecification httpRequest = RestAssured.given();
        Response response = httpRequest.request(Method.GET, "https://api.hh.ru/vacancies?text=java");
        Assert.assertEquals(response.statusCode(), 200);
        Assert.assertEquals(response.getStatusLine(), "HTTP/1.1 200 OK");
        Assert.assertNotNull(response.jsonPath().get("items").getClass());
    }
    @Test
    void RandomText() {
        RequestSpecification httpRequest = RestAssured.given();
        Response response = httpRequest.request(Method.GET, "https://api.hh.ru/vacancies?text=fsdsfewe");
        Assert.assertEquals(response.statusCode(), 200);
        Assert.assertEquals(response.getStatusLine(), "HTTP/1.1 200 OK");
        Assert.assertNotNull(response.jsonPath().get("items").getClass());
    }
    @Test
    void EmptyText() {
        RequestSpecification httpRequest = RestAssured.given();
        Response response = httpRequest.request(Method.GET, "https://api.hh.ru/vacancies?text=");
        Assert.assertEquals(response.statusCode(), 200);
        Assert.assertEquals(response.getStatusLine(), "HTTP/1.1 200 OK");
    }
    @Test
    void MaxLengthGet32628Chars() {
        RequestSpecification httpRequest = RestAssured.given();
        Response response = httpRequest.request(Method.GET, "https://api.hh.ru/vacancies?" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "");
        Assert.assertEquals(response.statusCode(), 200);
        Assert.assertEquals(response.getStatusLine(), "HTTP/1.1 200 OK");
        Assert.assertNotNull(response.jsonPath().get("items").getClass());
    }
    @Test
    void NegativeGetLength32629Chars() {
          RequestSpecification httpRequest = RestAssured.given();
        Response response = httpRequest.request(Method.GET, "https://api.hh.ru/vacancies?" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "" +
                "!");
        Assert.assertEquals(response.statusCode(), 502);
        Assert.assertEquals(response.getStatusLine(), "HTTP/1.1 502 Bad Gateway");
    }
    @Test
    void SQLInjection() {
        RequestSpecification httpRequest = RestAssured.given();
        Response response = httpRequest.request(Method.GET, "https://api.hh.ru/vacancies?text=; \n" +"SELECT 1/0 FROM users");
        Assert.assertEquals(response.statusCode(), 200);
        Assert.assertEquals(response.getStatusLine(), "HTTP/1.1 200 OK");
        Assert.assertNotNull(response.jsonPath().get("items").getClass());
    }
    @Test
    void RuAndCheck() {
        RequestSpecification httpRequest = RestAssured.given();
        Response response = httpRequest.request(Method.GET, "https://api.hh.ru/vacancies?text=закупки AND канцтовары");
        Assert.assertEquals(response.statusCode(), 200);
        Assert.assertEquals(response.getStatusLine(), "HTTP/1.1 200 OK");
        Assert.assertNotNull(response.jsonPath().get("items").getClass());
    }
    @Test
    void Symbols() {
        RequestSpecification httpRequest = RestAssured.given();
        Response response = httpRequest.request(Method.GET, "https://api.hh.ru/vacancies?text=\uD835\uDCE3\uD835\uDCF1\uD835\uDCEE \uD835\uDCFA\uD835\uDCFE\uD835\uDCF2\uD835\uDCEC\uD835\uDCF4 \uD835\uDCEB\uD835\uDCFB\uD835\uDCF8\uD835\uDD00\uD835\uDCF7 \uD835\uDCEF\uD835\uDCF8\uD835\uDD01 \uD835\uDCF3\uD835\uDCFE\uD835\uDCF6\uD835\uDCF9\uD835\uDCFC \uD835\uDCF8\uD835\uDCFF\uD835\uDCEE\uD835\uDCFB \uD835\uDCFD\uD835\uDCF1\uD835\uDCEE \uD835\uDCF5\uD835\uDCEA\uD835\uDD03\uD835\uDD02 \uD835\uDCED\uD835\uDCF8\uD835\uDCF0");
        Assert.assertEquals(response.statusCode(), 200);
        Assert.assertEquals(response.getStatusLine(), "HTTP/1.1 200 OK");
        Assert.assertNotNull(response.jsonPath().get("items").getClass());
    }
    @Test
    void ScriptInjection() {
        RequestSpecification httpRequest = RestAssured.given();
        Response response = httpRequest.request(Method.GET, "https://api.hh.ru/vacancies?text=<a href=\"`'><script>\\x0Bjavascript:alert(1)</script>");
        Assert.assertEquals(response.statusCode(), 200);
        Assert.assertEquals(response.getStatusLine(), "HTTP/1.1 200 OK");
        Assert.assertNotNull(response.jsonPath().get("items").getClass());
    }
}


